```javascript
// ember/tests/integration/speaker-page-test.js

this.get('/api/speakers/:id', function(request) {
  var speaker = speakers.find(function(speaker) {
    if (speaker.id === parseInt(request.params.id, 10)) {
      return speaker;
    }
